targets.expr:
clamp_min(clamp_max((
  (
    sum by (runner_type) (
  rate(gitlab_ci_queue_retrieval_duration_seconds_bucket{env="$environment",environment="$environment",le="+Inf",runner_type!="",stage="$stage"}[$__interval])
)

    -
    (
      sum by (runner_type) (
  rate(gitlab_ci_queue_retrieval_duration_seconds_bucket{env="$environment",environment="$environment",le="0.5",runner_type!="",stage="$stage"}[$__interval])
)

    )
  )
  / ignoring(runner_type) group_left()
  (
    sum by () (
  rate(gitlab_ci_queue_retrieval_duration_seconds_bucket{env="$environment",environment="$environment",le="+Inf",runner_type!="",stage="$stage"}[$__interval])
)

  )
) > 0
,1),0)

clamp_min(clamp_max((
  (
    sum by (jobs_running_for_project) (
  rate(job_queue_duration_seconds_bucket{env="$environment",environment="$environment",jobs_running_for_project!="",le="+Inf",shared_runner="true",stage="$stage"}[$__interval])
)

    -
    (
      sum by (jobs_running_for_project) (
  rate(job_queue_duration_seconds_bucket{env="$environment",environment="$environment",jobs_running_for_project!="",le="60",shared_runner="true",stage="$stage"}[$__interval])
)

    )
  )
  / ignoring(jobs_running_for_project) group_left()
  (
    sum by () (
  rate(job_queue_duration_seconds_bucket{env="$environment",environment="$environment",jobs_running_for_project!="",le="+Inf",shared_runner="true",stage="$stage"}[$__interval])
)

  )
) > 0
,1),0)

clamp_min(
  clamp_max(
    max by(environment, tier, type, stage, job, instance) (
  clamp_min(
    clamp_max(
      (
        process_open_fds{env="$environment",environment="$environment",stage="$stage",type="ci-runners"}
        /
        process_max_fds{env="$environment",environment="$environment",stage="$stage",type="ci-runners"}
      )
      or
      (
        ruby_file_descriptors{env="$environment",environment="$environment",stage="$stage",type="ci-runners"}
        /
        ruby_process_max_fds{env="$environment",environment="$environment",stage="$stage",type="ci-runners"}
      )
      ,
      1)
  ,
  0)
)

  ,1)
,0)
