  gitlab_service_apdex:ratio_5m{environment="$environment",monitor="global",stage="$stage",tier="runners",type="ci-runners"} offset 1w
,
  scalar(min((1 - 14.4 * (1 - avg(slo:min:events:gitlab_service_apdex:ratio{component="",monitor="global",tier="runners",type="ci-runners"})
))
))
)

